import React, { useState, useRef } from 'react';

export default function WeightExercise(props) {
  const { name, images } = props.selectedWorkout;
  const [weight, setWeight] = useState(0);
  const [reps, setReps] = useState(0);
  const [holdTime, setHoldTime] = useState(0);
  const [time, setTime] = useState(0);
  const [status, setStatus] = useState('lift');
  const [running, setRunning] = useState(false);
  const intervalRef = useRef(null);

  function startTimer() {
    if (!running) {
      intervalRef.current = setInterval(function () {
        setTime(function (prevTime) {
          return prevTime + 10;
        });
      }, 10);
      setRunning(true);
    }
  }

  function stopTimer() {
    clearInterval(intervalRef.current);
    setRunning(false);
    if (status === 'lift') {
      setStatus('hold');
      setTime(0);
    } else {
      setStatus('break');
      setTime(0);
    }
  }

  function resetTimer() {
    clearInterval(intervalRef.current);
    setTime(0);
    setRunning(false);
    setStatus('lift');
    setWeight(0);
    setReps(0);
    setHoldTime(0);
  }

  function handleWeightChange(event) {
    setWeight(event.target.value);
  }

  function handleRepsChange(event) {
    setReps(event.target.value);
  }

  function handleHoldTimeChange(event) {
    setHoldTime(event.target.value);
  }

  function formatTime(time) {
    const date = new Date(time);
    return date.toISOString().substring(11, 23);
  }

  return (
    <div>
      <h1>{name}</h1>
      <img src={images[status]} alt={name} />
      <div className='input-container'>
        <label>
          Weight:
          <input type='number' value={weight} onChange={handleWeightChange} />
        </label>
        <label>
          Reps:
          <input type='number' value={reps} onChange={handleRepsChange} />
        </label>
        <label>
          Hold Time:
          <input type='number' value={holdTime} onChange={handleHoldTimeChange} />
        </label>
      </div>
      <p>Time: {formatTime(time)}</p>
      <div className='options'>
        <button onClick={startTimer}>Start</button>
        <button onClick={stopTimer}>Stop</button>
        <button onClick={resetTimer}>Reset</button>
        <button onClick={() => window.location.reload()}>Back</button>
      </div>
    </div>
  );
}
