import React, { useState } from "react";
import DurationExercise from "./DurationExercise";
import RepetitionExercise from "./RepetitionExercise";
import WeightExercise from "/Users/mmya2/";

function ExerciseSet({ workouts }) {
  const [currentExercise, setCurrentExercise] = useState(0);

  const handleNextExercise = () => {
    setCurrentExercise((prev) => prev + 1);
  };

  const handlePrevExercise = () => {
    setCurrentExercise((prev) => prev - 1);
  };

  const handleSelectExercise = (index) => {
    setCurrentExercise(index);
  };

  const currentWorkout = workouts[currentExercise];

  let exerciseComponent;

  if (currentWorkout.type === "duration") {
    exerciseComponent = <DurationExercise name={currentWorkout.name} img={currentWorkout.img} />;
  } else if (currentWorkout.type === "repetition") {
    exerciseComponent = <RepetitionExercise name={currentWorkout.name} />;
  } else if (currentWorkout.type === "weights") {
    exerciseComponent = <WeightExercise name={currentWorkout.name} />;
  }

  return (
    <div>
      <h1>{currentWorkout.name}</h1>
      {exerciseComponent}
      <div className="button-row">
        <button disabled={currentExercise === 0} onClick={handlePrevExercise}>
          Prev
        </button>
        {workouts.map((workout, index) => (
          <button
            key={workout.name}
            className={currentExercise === index ? "selected" : ""}
            onClick={() => handleSelectExercise(index)}
          >
            {workout.name}
          </button>
        ))}
        <button
          disabled={currentExercise === workouts.length - 1}
          onClick={handleNextExercise}
        >
          Next
        </button>
      </div>
    </div>
  );
}

export default ExerciseSet;
