import React, { useState, useEffect } from 'react';

export default function WeightExercise(props) {
  // Destructure the props to extract the name and image
  const { name, img } = props.selectedWorkout;

  // State variables to keep track of the input values and exercise state
  const [weight, setWeight] = useState(0);
  const [reps, setReps] = useState(0);
  const [holdTime, setHoldTime] = useState(0);
  const [isExercising, setIsExercising] = useState(false);
  const [exerciseState, setExerciseState] = useState('lifting');
  const [timer, setTimer] = useState(0);

  // Function to handle the form submission
  const handleSubmit = (event) => {
    event.preventDefault(); // prevent the default form submission behavior
    setIsExercising(true); // start exercising
    setReps(reps - 1); // decrement the reps by 1
    setTimer(3); // set countdown to 3 seconds
  };

  // Function to handle the weight input change
  const handleWeightChange = (event) => {
    setWeight(event.target.value);
  };

  // Function to handle the reps input change
  const handleRepsChange = (event) => {
    setReps(event.target.value);
  };

  // Function to handle the hold time input change
  const handleHoldTimeChange = (event) => {
    setHoldTime(event.target.value);
  };

  // Exercise state loop effect
  useEffect(() => {
    let timerId;
    if (isExercising && reps > 0) {
      if (exerciseState === 'lifting') {
        // Countdown effect for lifting
        if (timer > 0) {
          timerId = setTimeout(() => {
            setTimer(timer - 1);
          }, 1000);
        } else {
          setExerciseState('holding');
          setTimer(holdTime); // start holding for the specified hold time
        }
      } else if (exerciseState === 'holding') {
        // Countdown effect for holding
        if (timer > 0) {
          timerId = setTimeout(() => {
            setTimer(timer - 1);
          }, 1000);
        } else {
          setExerciseState('breaking');
          setTimer(60); // take a 60 second break
        }
      } else {
        // Breaking state
        if (timer > 0) {
          timerId = setTimeout(() => {
            setTimer(timer - 1);
          }, 1000);
        } else {
          setIsExercising(false); // end the exercise loop
        }
      }
    }
    return () => {
      clearTimeout(timerId);
    };
  }, [isExercising, reps, exerciseState, timer, holdTime]);

  // Function to handle the restart button click
  const handleRestartClick = () => {
    setIsExercising(false); // end the exercise loop
    setReps(reps + 1); // increment the reps by 1
  };
  return (
  <div>
    <h2>{name}</h2>
    <img src={isLifting ? img[1].name : isHolding ? img[0].name : img[2].name} alt={name} />

    <form onSubmit={handleSubmit}>
      <label>
        Weight:
        <input type="number" value={weight} onChange={handleWeightChange} />
      </label>
      <br />
      <label>
        Reps:
        <input type="number" value={reps} onChange={handleRepsChange} />
      </label>
      <br />
      <label>
        Hold time:
        <input type="number" value={holdTime} onChange={handleHoldTimeChange} />
      </label>
      <br />
      {reps > 0 && (
        <div>
          {isLifting && <p>Lifting {timer}...</p>}
          {isHolding && <p>Hold for {timer}...</p>}
          {isBreaking && (
            <div>
              <p>Break for 60 seconds...</p>
              <button onClick={handleRestartClick}>Restart</button>
            </div>
          )}
        </div>
      )}
      {!isLifting && !isHolding && !isBreaking && reps === 0 && (
        <button type="submit">Start exercise</button>
      )}
    </form>
  </div>
);
      }
