import React, { useState } from 'react';

function ExerciseSet(props) {
  const [workouts] = useState(props.workouts);
  const [selectedWorkouts, setSelectedWorkouts] = useState([]);

  const handleWorkoutClick = (workout) => {
    setSelectedWorkouts((prevSelectedWorkouts) => [...prevSelectedWorkouts, workout.name]);
  };

  const renderWorkouts = () => {
    return workouts.map((workout) => (
      <li key={workout.name}>
        <button onClick={() => handleWorkoutClick(workout)}>{workout.name}</button>
      </li>
    ));
  };

  const renderSelectedWorkouts = () => {
    return selectedWorkouts.map((workout) => <li key={workout}>{workout}</li>);
  };

  const clearSelectedWorkouts = () => {
    setSelectedWorkouts([]);
  };

  return (
    <div>
      <h2>Make a customized set by selecting from the following workouts</h2>

      <div className='workouts'>
        <h3>Select Workouts</h3>
        <ul>{renderWorkouts()}</ul>
      </div>

      <div className='selected-workouts'>
        <h3>Selected Workouts</h3>
        <ul>{renderSelectedWorkouts()}</ul>
        {selectedWorkouts.length > 0 && (
          <button onClick={clearSelectedWorkouts}>Clear Selection</button>
        )}
      </div>

      <button onClick={() => window.location.reload()}>Back</button>
    </div>
  );
}

export default ExerciseSet;
